import React from 'react'
import Head from 'next/head'
import Nav from '../../components/nav'
import * as firebase from 'firebase';
import loadFirebase from '../../lib/db'
import withRouter from 'next/router'

class Note extends React.Component {
    static async getInitialProps(context) {
        const id = context.query.id;
        return {id}
    }

    // componentDidMount() {
    //     const { id } = this.props.id;
    //     if (!firebase.apps.length) {
    //         firebase.initializeApp(clientCredentials);
    //     }
    //     const rootRef = firebase.database().ref().child('notes/' + id);
    //     const titleRef = rootRef.child('title');
    //     const contentRef = rootRef.child('content');
    //     var title = '';
    //     var content = '';
    //     titleRef.on('value', snap => {
    //         title = snap.val();
    //     });
    //     contentRef.on('value', snap => {
    //         content = snap.val();
    //     });

    //     return {id, title, content};
    // }

    constructor(props) {
        super(props);
        console.log(props.query);
        this.state = {
            id: this.props.id,
            title: '',
            content: ''
        }
    }

    async componentDidMount() {
        const firebase = await loadFirebase();
        const db = firebase.firestore();
        console.log(this.state.id);

        let noteRef = db.collection('notes');
        let note = noteRef.doc(this.props.id).get()
        .then(snapshot => {
            console.log("snapshot", snapshot);
          snapshot.forEach(doc => {
            this.setState({
                title: doc.data().title,
                content: doc.data().content
            })
          });
          console.log("CDM state",this.state);
        })
        .catch(err => {
          console.log('Error getting documents', err);
        });

    }

    render() {
        return (
            <div>
                <Head>
                <title>Note</title>
                <link rel="icon" href="/favicon.ico" />
                </Head>

                <Nav />

                <h1>Title: {this.props.title}</h1>
                <p>{this.props.content}</p>

            </div>
        )
    }

}

export default Note
