import React from 'react'
import Head from 'next/head'
import Nav from '../components/nav'
import Link from 'next/link'
import loadFirebase from '../lib/db'

class Notes extends React.Component {
    // static async getInitialProps() {
    //     if (!firebase.apps.length) {
    //         firebase.initializeApp(clientCredentials);
    //     }
    //     const rootRef = firebase.database().ref().child('notes/1');
    //     const titleRef = rootRef.child('title');
    //     var title = '';
    //     titleRef.once('value', snap => {
    //         title = snap.val();
    //     });
    //     console.log(title);
    //     return {title};
    // }
    constructor() {
        super();
        this.state = {
            title: '',
            content: ''
        }
    }

    async componentDidMount() {
        const firebase = await loadFirebase();
        const db = firebase.firestore();
        let notesRef = db.collection('notes');
        let allNotes = notesRef.get()
        .then(snapshot => {
          snapshot.forEach(doc => {
            this.setState({
                title: doc.data().title,
                content: doc.data().content
            })
            console.log(doc.id, '=>', doc.data());
          });
        })
        .catch(err => {
          console.log('Error getting documents', err);
        });
    }

    render() {
        return(
            <div>
                <Head>
                <title>Notes</title>
                <link rel="icon" href="/favicon.ico" />
                </Head>

                <Nav />

                <h1>Notes</h1>
                <Link href="/note/[id]" as="/note/1">
                    <a>{this.state.title}</a>
                </Link>
            </div>
        )
    }
}

export default Notes
