import React from 'react'
import Head from 'next/head'
import Nav from '../components/nav'
import Link from 'next/link'
import ButtonAppBar from '../components/buttonAppBar'
import fetch from 'isomorphic-unfetch'

class Recharge extends React.Component{
    static async getInitialProps() {
        const quoteRes = await fetch('https://michael-scott-quotes.herokuapp.com/quote');
        const quoteData = await quoteRes.json();
        const quote = quoteData.quote;
        const escapedQuote = encodeURI(quote + ' michael scott');
        const gifRes = await fetch('http://api.giphy.com/v1/gifs/search?api_key=dc6zaTOxFJmzC&q=' + escapedQuote);
        const gifData = await gifRes.json();
        const gif = gifData.data[0].images.original_mp4.mp4;
        return {quote, gif}
    }
    render() {
        return (
            <div>
                <Head>
                <title>Recharge</title>
                <link rel="icon" href="/favicon.ico" />
                </Head>
                <ButtonAppBar title='Recharge'/>
                <Nav />

                <h1>Michael Scott Quote</h1>
                <p>{this.props.quote}</p>
                <p>{this.props.gif}</p>
                <video width="320" height="240" autoPlay>
                    <source src={this.props.gif} type="video/mp4" />
                    Your browser does not support the video tag.
                </video>
            </div>
        )
    }
}

export default Recharge
